pragma solidity ^0.5.0;

// Reading the Product
contract Marketplace {
    string public name; // State variable permanently stored in contract storage https://bit.ly/3pgZn6h
    uint256 public productCount = 0;
    mapping(uint256 => Product) public products; // think of mapping as key-to-value storage, e.g. Dicts, Maps

    struct Product {
        // We can treat a struct like a value type such that they can be used within arrays and mappings.
        uint256 id; // List the struct members, which consists of variables names along with their types
        string name;
        uint256 price;
        address payable owner;
        bool purchased;
    }

    event ProductAdded(
        uint256 id,
        string name,
        uint256 price,
        address payable owner,
        bool purchased
    );

    event ProductPurchased(
        uint256 id,
        string name,
        uint256 price,
        address payable owner,
        bool purchased
    );

    constructor() public {
        name = "Nakamoto List";
    }

    // Function to creat a product
    // _ underscores indicate local variables
    function addProduct(string memory _name, uint256 _price) public {
        // Require a valid name
        require(bytes(_name).length > 0);
        // Require a valid price
        require(_price > 0);
        // Increment product count
        productCount++;
        // Create the product
        products[productCount] = Product(
            productCount,
            _name,
            _price,
            msg.sender,
            false
        );
        // Trigger an event
        emit ProductAdded(productCount, _name, _price, msg.sender, false);
    }

    function buyProduct(uint256 _id) public payable {
        /*Fetch product (memory keyword: https://bit.ly/3g6fHTn)
        memory cannot be used at the contract level, only in methods.*/
        Product memory _product = products[_id];
        //Fetch the owner
        address payable _seller = _product.owner;
        //Make sure product is valid (can be bought)
        //Purchase product / transfer ownsership
        _product.owner = msg.sender;
        //Mark as bought
        _product.purchased = true;
        //Update contract
        products[_id] = _product;
        //Pay seller with Ether
        address(_seller).transfer(msg.value);
        //Trigger an event
        emit ProductPurchased(productCount, _product.name, _product.price, msg.sender, false);
    }
}
